#!/usr/bin/env python3import jsonimport pkg_resourcesimport loggingfrom logging.config import dictConfigfrom dmdpy.protein import atom, chain, residue, proteinlogger = logging.getLogger(__name__)def load_logger_config():    """Loads in the Logger Config File"""    try:        with open(pkg_resources.resource_filename('dmdpy.resources', 'logger_config.json')) as logger_config:            config = json.load(logger_config)        dictConfig(config)        logger.debug("Loaded in logger parameters")        logger.debug("Logger has started!")    except IOError:        logger.critical("Could not open logger_config.json")        raise    except ValueError:        logger.critical("logger_config.json is not formatted properly")        raisedef load_dmd_config():    try:        logger.debug("Loading in the dmd_config parameters")        with open(pkg_resources.resource_filename('dmdpy.resources', 'dmd_config.json')) as logger_config:            config = json.load(logger_config)        return config    except IOError:        logger.critical("Could not open dmd_config.json")        raise    except ValueError:        logger.critical("dmd_config.json is not formatted properly")        raisedef load_pdb(file: str):    logger.debug(f"Finding file: {file}")    chains = []    resNum = 0    chainLet = ""    lineNumber = 1    with open(file, 'r') as pdb:        for line in pdb:            if "ATOM" in line or "HETATM" in line:                tmpAtom = atom.Atom(line)                if chainLet != line[21:22]:                    chainLet = line[21:22]                    chains.append(chain.Chain(chainLet))                if resNum != int(line[22:26]):                    resNum = int(line[22:26])                    chains[-1].add_residue(residue.Residue(line))                chains[-1].residues[-1].add_atom(tmpAtom)            lineNumber += 1    return protein.Protein(file, chains)# def make_start_file(dmd_parameters: dict):#     logger.debug("I am making the parameters file!!!")